// Menu script
  var i = document.getElementById("menu").style; //select the menu div
  if (document.addEventListener) {
    //show menu
    document.addEventListener('contextmenu', function(e) {
      var posX = e.clientX;
      var posY = e.clientY;
      menu(posX, posY);
      e.preventDefault();
    }, false);
    document.addEventListener('click', function(e) {
      i.opacity = "0";
      setTimeout(function() {
        i.visibility = "hidden";
      }, 501);
    }, false);
  } else {
    document.attachEvent('oncontextmenu', function(e) {
      var posX = e.clientX;
      var posY = e.clientY;
      menu(posX, posY);
      e.preventDefault();
    });
    document.attachEvent('onclick', function(e) { 
      i.opacity = "0";
      setTimeout(function() {
        i.visibility = "hidden";
      }, 501);
    });
  }
  
  //get the position for the menu function
  function menu(x, y) {
    i.top = y + "px";
    i.left = x + "px";
    i.visibility = "visible";
    i.opacity = "1";
  }

  /* ============ FUNCTIONS FOR OPTIONS ============== */
  function redirect(link) { //you can also use the "href='...' " , this is just for example
    window.open(link)
  }
  
  